mapper.select:
SELECT SUM(product_stock) as product_stock FROM ( SELECT product.product_id, product.product_name, product.product_price, ifnull(SUM(product_receiving.receiving_num),0) AS product_stock FROM product LEFT OUTER JOIN product_receiving ON product.product_id = product_receiving.product_id GROUP BY product.product_id, product.product_name, product.product_price
UNION all
SELECT product.product_id, product.product_name, product.product_price, ifnull(SUM(product_sell.product_num)*-1,0) AS product_stock FROM product LEFT OUTER JOIN product_sell ON product.product_id = product_sell.product_id GROUP BY product.product_id, product.product_name, product.product_price )A GROUP BY product_id, product_name, product_price HAVING product_id = #{product_id}
